function fixAdopted(str:string = ""): Date {
  return new Date(str.replace(/^Adopted\s+/i, "").trim());
}

interface AdoptedDog {
  name: string;
  adopted: Date;
  url: URL;
}

const data = dogs.galleryImages
  .reduce((acc: AdoptedDog[], dog: any) => {
    let [name, adopted]: [string, string] = (dog.image.alt ?? "")
      .trim()
      .split("\n");
    if (dog.caption) {
      [name, adopted] = JSON.parse(dog.caption)
        .blocks.map((b) => b.text)
        .join("\n")
        .trim()
        .split("\n");
    }
    acc.push({
      name: name.trim(),
      // NOTE: Remove the "Adopted " prefix.
      adopted: fixAdopted(adopted),
      // NOTE: image URLs seem to be protocol-less, so inject an HTTPS: protocol.
      url: new URL(`https:${dog.image.image}`),
    });
    return acc;
  }, [])
  // Sort by adopted date, ascending.
  .sort(
    (a: AdoptedDog, b: AdoptedDog) => a.adopted.valueOf() - b.adopted.valueOf()
  )
  // HACK: A mystery dog doesn't have a name or adopted date.
  .filter((dog: AdoptedDog) => dog.name);

await fs.writeFile("./adopted.json", JSON.stringify(data, null, 2));
